example.code:
$("#myform").validate();
 // this could be called after some user interaction
 $("#myform").valid()

function countUnchecked() {
  var n = $("input:unchecked").length;
  $("div").text(n + (n == 1 ? " is" : " are") + " unchecked!");
}
countUnchecked();
$(":checkbox").click(countUnchecked);

jQuery.validator.addMethod("domain", function(value) { 
  return this.optional(element) || /^http://mycorporatedomain.com/.test(value); 
}, "Please specify the correct domain for your documents");

jQuery.validator.addMethod("math", function(value, element, params) { 
 return this.optional(element) || value == params[0] + params[1]; 
}, String.format("Please enter the correct value for {0} + {1}"));

